import { TodoSearch } from './TodoSearch';
import { TodoList } from './TodoList';
import { CreateTodoButton } from './CreateTodoButton';
import './App.css';

function App() {
	return (
		<div className="App">
			<TodoCounter />
			<TodoSearch />

			<TodoList>
				<TodoItem />
				<TodoItem />
				<TodoItem />
			</TodoList>

			<CreateTodoButton />
		</div>
	);
}
const totalTodos = 5;
const pendingTodos = totalTodos - todosCompleted;
const todosCompleted = 2;

function TodoItem() {
	return (
		<li>
			<span>✅</span>
			<p>Launch a Million dollar business</p>
			<span>❌</span>
		</li>
	);
}

export default App;
